class Solution {
public:
    #define ll long long
    const int mod = 1e9 + 7;
    ll dp[2001][51][51];
    ll helper(int idx, vector<int>& incr, vector<int>& decr, vector<int>& nums) {
        if(idx == nums.size()) return 1;
        ll curr = 0;
        if(idx > 0 && dp[idx][incr[idx - 1]][decr[idx - 1]] != -1) return dp[idx][incr[idx - 1]][decr[idx - 1]];
        for(int i = 0; i <= nums[idx]; i++) {
            if(idx > 0 && i >= incr[idx - 1]) incr[idx] = i;
            else if(idx == 0) incr[idx] = i;
            else continue;

            int rem = nums[idx] - i;
            if(idx > 0 && rem <= decr[idx - 1]) decr[idx] = rem;
            else if(idx == 0) decr[idx] = rem;
            else continue;

            curr = (curr + helper(idx + 1, incr, decr, nums)) % mod;
        }
        //cout << curr << " curr" <<endl;
        if(idx == 0) return curr;
        return dp[idx][incr[idx - 1]][decr[idx - 1]] = curr;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp, -1, sizeof(dp));
        ll ans = 0;
        vector<int> incr(n, -1), decr(n, -1);
        return helper(0, incr, decr, nums);
    }
};
