class Solution {
public:
    vector<vector<int>> dp;
    int mod = 1e9+7;
    long long rec(vector<int>& nums,int index,int pG,int pL,int n) {
        if(index == n) {
            return 1;
        }
        if(dp[index][pG]!=-1) return dp[index][pG];


        long long ans  = 0;
        // cout<<index<<" "<<pG<<" "<<pL<<endl;

        for(int i=0;i<=nums[index];i++){
            int nG = i;
            int nL = nums[index] - i ;
            if(nG >= pG && nL <= pL ) {
                ans += rec(nums,index+1,nG,nL,n);
                ans = ans%mod;
            }
        }

        return dp[index][pG] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        dp.resize(n,vector<int>(51,-1));

        return rec(nums,0,0,nums[0]+1,n)%mod;
    }
};
