class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        ans = 0
        n = len(nums)

        MOD = 10**9 + 7

        @cache
        def dp(i, prev_a, prev_b):
            if i == n:
                return 1

            t_cnt = 0
            for na in range(prev_a, nums[i] + 1):
                nb = nums[i] - na
                if nb >= 0 and nb <= prev_b:
                    t_cnt += dp(i + 1, na, nb)
            return t_cnt % MOD

        return dp(0, 0, 50)
