class Solution {
public:
    int countOfPairs(vector<int>& numbers) {
        const int MOD = 1e9 + 7;
        int n = numbers.size();
        vector<vector<vector<int>>> dp(n + 1, vector<vector<int>>(51, vector<int>(51, -1)));
        return computeTotalPairs(numbers, dp, MOD);
    }

private:
    int computeTotalPairs(const vector<int>& numbers, vector<vector<vector<int>>>& dp, int mod) {
        return computePairsRecursively(0, 0, 50, numbers, dp, mod);
    }

    int computePairsRecursively(int index, int lastFirstSeq, int lastSecondSeq, const vector<int>& numbers, vector<vector<vector<int>>>& dp, int mod) {
        if (index == numbers.size()) {
            return 1;
        }

        if (isMemoized(dp, index, lastFirstSeq, lastSecondSeq)) {
            return getMemoizedValue(dp, index, lastFirstSeq, lastSecondSeq);
        }

        int totalCount = 0;
        for (int x = 0; x <= numbers[index]; x++) {
            int y = calculateSecondValue(numbers[index], x);
            if (isValidPair(x, y, lastFirstSeq, lastSecondSeq)) {
                totalCount = (totalCount + computePairsRecursively(index + 1, x, y, numbers, dp, mod)) % mod;
            }
        }

        memoize(dp, index, lastFirstSeq, lastSecondSeq, totalCount);
        return totalCount;
    }

    bool isMemoized(const vector<vector<vector<int>>>& dp, int index, int lastFirstSeq, int lastSecondSeq) const {
        return dp[index][lastFirstSeq][lastSecondSeq] != -1;
    }

    int getMemoizedValue(const vector<vector<vector<int>>>& dp, int index, int lastFirstSeq, int lastSecondSeq) const {
        return dp[index][lastFirstSeq][lastSecondSeq];
    }

    int calculateSecondValue(int currentValue, int firstSeqValue) const {
        return currentValue - firstSeqValue;
    }

    bool isValidPair(int x, int y, int lastFirstSeq, int lastSecondSeq) const {
        return x >= lastFirstSeq && y <= lastSecondSeq;
    }

    void memoize(vector<vector<vector<int>>>& dp, int index, int lastFirstSeq, int lastSecondSeq, int totalCount) {
        dp[index][lastFirstSeq][lastSecondSeq] = totalCount;
    }
};
