class Solution {
public:
    int helper(int i, int last1, int last2, vector<int>& nums, vector<vector<int>>& dp){
        if(i == nums.size()) return 1;
        if(dp[i][last1] != -1) return dp[i][last1];
        int s = 0, e = nums[i];
        int ans = 0;
        while(s <= e){
            if(s >= last1 && e <= last2){
                 ans = (ans + helper(i + 1, s, e, nums, dp)) % 1000000007;
            }
            if(s <= last2 && e >= last1 && s != e){
                ans = (ans + helper(i + 1, e, s, nums, dp)) % 1000000007;
            }
            s++;
            e--;
        }
        return dp[i][last1] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(2001, vector<int>(51, -1));
        return helper(0, 0, 50, nums, dp);
    }
};
