class Solution {
public:
    int mod = (int)(1e9 + 7);
    int f(int i, long long pa, long long pb, vector<int>&v, int n, vector<vector<vector<long long>>>&dp)
    {
        if(i == n) return dp[i][pa][pb] = 1;
        if(dp[i][pa][pb] != -1) return (int)dp[i][pa][pb];
        long long x = v[i];
        long long sum = 0;
        for(long long can = pa; can <= x; can++)
        {
            if(pa<= can && (x - can)<= pb) sum = ((sum % mod) +  (f(i+1, can, x - can, v, n, dp) % mod))%mod;
        }
        return dp[i][pa][pb] = sum;
    }
    int countOfPairs(vector<int>& nums) 
    {
        int n = nums.size();
        vector<vector<vector<long long>>>dp(n+1, vector<vector<long long>>(51, vector<long long>(51, -1)));
        return f(0, 0, 50, nums, n, dp);

    }
};
