class Solution {
public:
    int MOD = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<int> arr1(n);
        vector<int> arr2(n);
        vector<vector<int>> dp(n, vector<int>(51, 0));
        for (int j = 0; j <= nums[0]; j++)
            dp[0][j] = 1;
        for (int i = 1; i < n; i++){
            for (int j = 0; j <= nums[i]; j++){
                // j  nums[i] - j
                int ways = 0;
                for (int k = 0; k <= 50; k++){
                    if (k <= j && nums[i - 1] - k >= nums[i] - j)
                        ways = (ways + dp[i - 1][k]) % MOD;
                }
                dp[i][j] = ways;
            }
        }
        int res = 0;
        for (int i = 0; i <= 50; i++)
            res = (res + dp[n - 1][i]) % MOD;
        return res;
    }
};
