class Solution {
public:
    const int MOD = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int max_val = 50;

        vector<vector<vector<int>>> dp(n, vector<vector<int>>(max_val+1, vector<int>(max_val+1,0)));

        for (int j = 0; j <= nums[0]; ++j) {
            int arr2_0 = nums[0] - j;
            if (arr2_0 >= 0) {
                dp[0][j][arr2_0] = 1;
            }
        }

        for (int i = 1; i < n; ++i) {
            for (int prev1 = 0; prev1 <= max_val;prev1++) {
                for (int prev2 = 0; prev2 <= max_val;prev2++) {
                    if (dp[i-1][prev1][prev2] == 0) continue;

                    for (int j = prev1; j <= nums[i]; ++j) {
                        int arr2_i = nums[i] - j;
                        if (arr2_i <= prev2 && arr2_i >= 0) {
                            dp[i][j][arr2_i] = (dp[i][j][arr2_i] + dp[i-1][prev1][prev2]) % MOD;
                        }
                    }
                }
            }
        }

        int result = 0;
        for (int j = 0; j <= nums[n-1]; j++) {
            int arr2_last = nums[n-1] - j;
            if (arr2_last >= 0) {
                result = (result + dp[n-1][j][arr2_last]) % MOD;
            }
        }

        return result;
    }
};
