const int MOD = 1e9 + 7;
class Solution {
public:
    int rec(int i ,int x,int y,vector<int>& nums,vector<vector<vector<int>>>& dp)
    {
        // base
        if(i == nums.size()) return 1;
        // trans
        if(dp[i][x][y] != -1) return dp[i][x][y];
        // min value 0 - max value 50
        int cnt = 0;
        for(int val = x; val <= 50; val++)
        {
            if(nums[i]-val <= y && nums[i]-val >= 0)
            {
                cnt = (cnt + rec(i+1,val,nums[i]-val,nums,dp))%MOD;
            }
        }

        return dp[i][x][y] = cnt%MOD;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n,vector<vector<int>>(51,vector<int>(51,-1)));
        return rec(0,0,nums[0],nums,dp);
    }
};
