class Solution {
public:
    int mod=1e9+7;
    int helper(int ind,int prevl,int prevr,int N,vector<int>&nums,vector<vector<int>>&dp){
        if(ind==N){
            return 1;
        }

        // cout<<ind<<" "<<N<<" "<<prevl<<endl; 
        if(dp[ind][prevl+1]!=-1)return dp[ind][prevl+1];

        int tot=0;
        for(int n=0;n<=nums[ind];n++){
            int n2=nums[ind]-n;
            if(n>=prevl and n2<=prevr){
                tot=(tot%mod+helper(ind+1,n,n2,N,nums,dp)%mod)%mod;
            }
        }

        return dp[ind][prevl+1]=tot;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int>> dp(n,vector<int>(53,-1));

        return helper(0,-1,51,n,nums,dp);

    }
};
