class Solution {
public:
    const int mod = 1000000007;
    int memo[2005][52][52];

    int countOfPairs(vector<int>& nums) 
    {
        int n= nums.size();
        memset(memo, -1, sizeof(memo));

        return dp(0, 0, nums[0], n, nums);
    }

private:
    int dp(int idx, int v1, int v2, int n, vector<int>& nums)
    {
        if(idx >= n)    return 1;
        int res= 0;

        if(memo[idx][v1][v2] != -1)
            return memo[idx][v1][v2];

        for(int i=v1; i<= nums[idx]; i++){
            for(int j= v2; j>=0; j--)
            {
                if(i + j == nums[idx])
                {
                    res+= dp(idx+1, i, j, n, nums);
                    res %= mod;
                }
            }
        }

        return memo[idx][v1][v2] = res;
    }
};
