class Solution {
public:
    int dp[2005][52][52];
    void customMem(int n){
        for(int i=0;i<=n;i++)
            for(int j=0;j<=51;j++)
                for(int k=0;k<=51;k++)
                        dp[i][j][k]=-1;
    }
    int recur(int pos, vector<int>&nums, int a,int b){
        if(pos==nums.size()) return 1;
        int &ret = dp[pos][a][b];
        if(~ret) return ret;
        ret=0;
        int i=nums[pos],j=0;
        while(i>=0){
            if(i>=a && j<=b)
                ret=(ret+recur(pos+1,nums,i,j))%1000000007;
            i--;
            j++;
        }
        return ret;
    }
    int countOfPairs(vector<int>& nums) {
        customMem(nums.size());
        return recur(0,nums,0,51);
    }
};
