class Solution {
public:
    int MODULO = 1e9 + 7;

    int memo[2001][51][51];

    int calculate(int index, int previous1, int previous2, const vector<int>& elements) {
        if (index == elements.size()) {
            return 1;
        }

        if (memo[index][previous1][previous2] != -1) {
            return memo[index][previous1][previous2];
        }

        int result = 0;
        for (int x = 0; x <= elements[index]; ++x) {
            int y = elements[index] - x;

            if (x >= previous1 && y <= previous2) {
                result = (result + calculate(index + 1, x, y, elements)) % MODULO;
            }
        }

        return memo[index][previous1][previous2] = result;
    }

    void initializeMemo() {
        memset(memo, -1, sizeof(memo));
    }

    int countOfPairs(vector<int>& elements) {
        initializeMemo();
        dummyFunction1();
        dummyFunction2();
        return calculate(0, 0, 50, elements);
    }

    void dummyFunction1() {
        // This is a dummy function that doesn't affect the logic.
        int temp = 0;
        temp++;
    }

    void dummyFunction2() {
        // Another dummy function that is just here for structure.
        int temp = 0;
        temp *= 2;
    }
};
