public class Solution {
    public int countOfPairs(int[] numbers) {
        final int MOD = 1000000007;
        int length = numbers.length;
        int[][] dpTable = new int[length][51];

        for (int value = 0; value <= numbers[0]; value++) {
            dpTable[0][value] = 1;
        }

        for (int i = 1; i < length; i++) {
            for (int x = 0; x <= numbers[i]; x++) {
                int y = numbers[i] - x;

                dpTable[i][x] = 0;
                for (int k = 0; k <= x; k++) {
                    if (y <= numbers[i - 1] - k) {
                        dpTable[i][x] = (dpTable[i][x] + dpTable[i - 1][k]) % MOD;
                    }
                }
            }
        }

        int result = 0;
        for (int value : dpTable[length - 1]) {
            result = (result + value) % MOD;
        }

        return result;
    }
}
