class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        large = 10**9+7
        n = len(nums)
        print(f"case={nums}")
        @functools.lru_cache(None)
        def dp(i,mn,mx):
            if i == n:
                return 1
            res = 0
            for next_min in range(mn,nums[i]+1):
                next_max = nums[i] - next_min
                if next_max > mx:
                    continue
                res += dp(i+1,next_min,next_max)
                res %= large
            #print(f"dp({i},{mn})={res}")
            return res
        return dp(0,0,nums[0])
