class Solution {
    public int countOfPairs(int[] nums) {
        long[][] dpp = new long[55][55];
        long mod = (long)(1e9 + 7);
        int n = nums.length;

        for(int j = 0 ; j <=nums[0] ; j++){
            for (int k = 0 ; k <=nums[0] ; k++){
                if (k + j==nums[0])
                    dpp[k][j] = 1L;
            }
        }
        for (int j = 50 ; j>=0 ; j--){
            dpp[0][j]=(dpp[0][j] + dpp[0][j +1])%mod;
        }
        for (int i = 1 ; i<=50  ; i++){
            for (int j = 50 ; j>=0 ; j--){
                dpp[i][j] = dpp[i][j]%mod + dpp[i - 1][j]%mod + dpp[i][j  + 1]%mod - dpp[i - 1][j + 1]%mod; 
                dpp[i][j]%=mod;
            }

        }
        long ans =0;
        if (n==1)
            return nums[0] + 1;
        for (int i = 1 ; i < n ; i++){
            ans = 0;
            long[][] dpn = new long[55][55];
            for (int j = 0 ; j<=nums[i] ; j++){
                for (int k = 0 ; k<=nums[i] ; k++){
                    if (j + k==nums[i]){
                        dpn[j][k] =dpn[j][k] + dpp[j][k]; 
                        ans = (ans%mod+ dpp[j][k]%mod);
                    }
                }
            }
            for (int j = 50 ; j>=0 ;j--){
                dpn[0][j]=(dpn[0][j]%mod + dpn[0][j + 1]%mod)%mod;
            }
            for (int k = 1 ;k <=50 ; k++){
                for (int j = 50 ; j>=0 ; j--){
                    dpn[k][j]=(dpn[k][j]%mod + dpn[k - 1][j]%mod + dpn[k][j + 1]%mod  - dpn[k - 1][j + 1])%mod;
                    dpn[k][j]%=mod;
                }
            }
            dpp = dpn;

        }
        ans = ans%mod;
        return (int)ans;


    }
}
