class Solution {
public:
    int t[2010][60];
    const int MOD = 1e9 + 7;
    int solve(vector<int>& nums , int idx , int prev_a)
    {
        if(idx >= nums.size())
        {
            return 1;
        }
        int ans = 0;
        if(t[idx][prev_a + 5] != -1)
        {
            return t[idx][prev_a + 5];
        }
        for(int i = 0; i <= nums[idx]; i++)
        {
            if(idx == 0)
            {
                ans = (ans + solve(nums , idx + 1 , i)) % MOD;
            }
            if(idx > 0 && i >= prev_a && (nums[idx] - i) <= (nums[idx - 1] - prev_a))
            {
                ans = (ans + solve(nums , idx + 1 , i)) % MOD;
            }
        }
        return t[idx][prev_a + 5] = ans % MOD;
    }
    int countOfPairs(vector<int>& nums) {
        memset(t, -1 , sizeof(t));
        return solve(nums , 0 , -1);
    }
};
