class Solution(object):
    def countOfPairs(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        MOD = (10**9) + 7
        n = len(nums)
        max_num = max(nums)

        dp = [[0] * (max_num + 1) for _ in range(n)]

        for j in range(nums[0] + 1):
            dp[0][j] = 1

        for i in range(1, n):
            for j in range(nums[i] + 1):
                arr2_i = nums[i] - j
                dp_sum = 0
                for k in range(j + 1):
                    if nums[i-1] - k >= arr2_i:
                        dp_sum += dp[i-1][k]
                        dp_sum %= MOD
                dp[i][j] = dp_sum

        return sum(dp[-1]) % MOD
