class Solution {
public:
    int countOfPairs(vector<int>& nums) {

        int mod = 1e9; mod+=7;

        int n = nums.size();
        vector<vector<int>> dp(n, vector<int> (51, 0));

        for(int i=0; i<=nums[0]; i++)
        {
            dp[0][i]++;
        }

        for(int i=1; i<n; i++)
        {

            for(int j=0; j<=50; j++)
            {
                int lst1 = j;
                int lst2 = nums[i-1]-lst1;

                for(int k = lst1; k<=nums[i]; k++)
                {
                    int ths2 = nums[i]-k;
                    if(ths2<=lst2)
                    {
                        dp[i][k]+=dp[i-1][j];
                        dp[i][k]%=mod;
                    }
                }

            }

        }

        int ans=0;
        for(int i=0; i<=50; i++)
        {
            ans+=dp[n-1][i];
            ans%=mod;
        }

        // for(auto it : dp)
        // {
        //     for(auto y : it)
        //     {
        //         cout<<y<<" ";
        //     }
        //     cout<<"\n";
        // }


        return ans;

    }
};
