class Solution {
public:
    int mod = 1e9 + 7;
    vector<vector<int>> dp;

    int solve(vector<int>& nums, int n, int ind, int val) {
        if (ind >= n) return 1;
        if (dp[ind][val] != -1) return dp[ind][val];

        int ans = 0;
        for (int i = val; i <= nums[ind]; i++) {
            int x = i;
            int y = nums[ind] - i;

            if (ind > 0 && y > nums[ind-1] - val) continue;

            ans = (ans + solve(nums, n, ind + 1, i)) % mod;
        }

        return dp[ind][val] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp = vector<vector<int>>(n, vector<int>(51, -1));
        return solve(nums, n, 0, 0);
    }
};
