class Solution {
public:
int dp[2001][51][51];int mod=1e9+7;
int solve(vector<int>& nums,int ind,int inc,int dec){
    if(ind>=nums.size())return 1;
    if(dp[ind][inc][dec]!=-1)return dp[ind][inc][dec];
    long long ans=0;
    for(int i=inc;i<=nums[ind];i++){
        for(int j=dec;j>=0;j--){
            if(i+j==nums[ind]){
                ans+=solve(nums,ind+1,i,j);
                ans=ans%mod;
            }
        }
    }
    return dp[ind][inc][dec]=ans;
}
    int countOfPairs(vector<int>& nums) {
        memset (dp,-1,sizeof(dp));
        return solve(nums,0,0,nums[0]);
    }
};
