class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        ll = len(nums)
        @lru_cache(maxsize=None)
        def getN(p, a1, a2):
            if p == ll: return 1
            ans = 0
            for i in range(nums[p], a1-1, -1):
                j = nums[p] - i
                if j > a2 and i>=0 and j>=0: break
                ans += getN(p+1, i, j)
            return ans
        a = getN(0, 0, nums[0]) % (10**9+7)
        return a
