class Solution {
public:
    int dp[52][52][2002];
    int mod = 1e9+7;
    int rec(int idx, int a, int b, vector<int>&nums,bool flag){
        if(idx>=nums.size()){
            return flag;
        }
        if(dp[a][b][idx]!=-1)return dp[a][b][idx];
        int res=0;
        bool not_pos=true;
        for(int val=a;val<=nums[idx];val++){
            if((nums[idx]-val)<=b){
                not_pos=false;
                res=(res+ rec(idx+1,val,nums[idx]-val,nums,flag))%mod;

            }
        }
        if(not_pos==true)
        return 0;
        return dp[a][b][idx] = res;
    } 


    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int ans=0;

        memset(dp,-1,sizeof(dp));
        for(int i=0;i<=nums[0];i++){
            ans  = (ans+rec(1,i,nums[0]-i,nums,1))%mod;
        }
        return ans;
    }
};
