#include <vector>
using namespace std;

class Solution {
public:
    int countOfPairs(vector<int>& nums) 
    {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<int>> dp(51, vector<int>(51, 0));

        for (int a = 0; a <= nums[0]; ++a)
        {
            dp[a][nums[0] - a] = 1;
        }

        for (int i = 1; i < n; ++i)
        {
            vector<vector<int>> nextDp(51, vector<int>(51, 0));
            vector<vector<int>> psum(51, vector<int>(51, 0));
            for (int b = 0; b <= 50; ++b)
            {
                for (int c = 50; c >= 0; --c)
                {
                    psum[b][c] = dp[b][c];
                    if (c < 50)
                    {
                        psum[b][c] = (psum[b][c] + psum[b][c + 1]) % MOD;
                    }
                }
            }

            for (int d = 0; d <= nums[i]; ++d)
            {
                int e = nums[i] - d;
                for (int f = 0; f <= d; ++f)
                {
                    nextDp[d][e] = (nextDp[d][e] + psum[f][e]) % MOD;
                }
            }
            dp = nextDp;
        }

        int result = 0;
        for (int g = 0; g <= 50; ++g)
        {
            for (int h = 0; h <= 50; ++h)
            {
                result = (result + dp[g][h]) % MOD;
            }
        }
        return result;
    }
};
