class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<int>> dp(51, vector<int>(51, 0));
        // Initialize dp for the first element
        for (int j = 0; j <= nums[0]; ++j) {
            dp[j][nums[0] - j] = 1;
        }

        // Process each subsequent element in nums
        for (int i = 1; i < n; ++i) {
            vector<vector<int>> new_dp(51, vector<int>(51, 0));
            for (int j = 0; j <= nums[i]; ++j) {
                int k = nums[i] - j;
                for (int prev_j = 0; prev_j <= j; ++prev_j) {
                    int prev_k = nums[i-1] - prev_j;
                    if (prev_k >= k) {
                        new_dp[j][k] = (new_dp[j][k] + dp[prev_j][prev_k]) % MOD;
                    }
                }
            }
            dp = new_dp;
        }

        // Sum all values in the last dp table
        int result = 0;
        for (int j = 0; j <= 50; ++j) {
            for (int k = 0; k <= 50; ++k) {
                result = (result + dp[j][k]) % MOD;
            }
        }

        return result;
    }
};
