class Solution {
public:
    long long int dp[2000][51][51]; 

    long long int shubham_solve(const vector<int>& nums, int i, int mini, int maxi) {
        if (i == nums.size()){
            return 1;
        } 
        if (dp[i][mini][maxi] != -1) return dp[i][mini][maxi]; 
        long long int ans = 0;
        for (int x = mini; x <= nums[i]; ++x) {
            for (int y = maxi; y >= 0; --y) {
                if (x + y == nums[i]) {
                    ans = (ans + shubham_solve(nums, i + 1, x, y)) % 1000000007;
                }
            }
        }

        return dp[i][mini][maxi] = ans; 
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp)); 
        return shubham_solve(nums, 0, 0, nums[0]);
    }
};
