class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1000000007;
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, 0)));


        for (int x = 0; x <= nums[0]; ++x) {
            int y = nums[0] - x;
            if (y >= 0 && y <= 50) {
                dp[0][x][y] = 1;
            }
        }
        for (int i = 1; i < n; ++i) {
            for (int x = 0; x <= nums[i]; ++x) {
                int y = nums[i] - x;
                if (y < 0 || y > 50)
                    continue;

                for (int px = 0; px <= nums[i - 1]; ++px) {
                    int py = nums[i - 1] - px;
                    if (py < 0 || py > 50)
                        continue;

                    if (px <= x && py >= y) {
                        dp[i][x][y] = (dp[i][x][y] + dp[i - 1][px][py]) % MOD;
                    }
                }
            }
        }

        int result = 0;
        for (int x = 0; x <= nums[n - 1]; ++x) {
            int y = nums[n - 1] - x;
            if (y >= 0 && y <= 50) {
                result = (result + dp[n - 1][x][y]) % MOD;
            }
        }

        return result;
    }
};
