class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        memo = vector<vector<int>>(n, vector<int>(1001, -1));

        return dfs(nums, 0, 0) % MOD;
    }

private:
    vector<vector<int>> memo;

    int dfs(const vector<int>& nums, int index, int prev) {
        if (index == nums.size()) {
            return 1;
        }

        if (memo[index][prev] != -1) {
            return memo[index][prev];
        }

        int result = 0;
        const int MOD = 1e9 + 7;

        for (int i = prev; i <= nums[index]; ++i) {
            int arr2_i = nums[index] - i; 
            if (index == 0 || arr2_i <= (nums[index-1] - prev)) {
                result = (result + dfs(nums, index + 1, i)) % MOD;
            }
        }

        return memo[index][prev] = result;
    }
};
