typedef long long int ll;
#define MOD 1000000007
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
          int size = nums.size();
        vector<vector<vector<ll>>> ghtiya(size, vector<vector<ll>>(51, vector<ll>(51, -1)));

        function<ll(int, int, int)> solve = [&](int index, int val1, int val2) -> ll {
            if (index >= nums.size()) return 1;
            ll result = 0;
            if (ghtiya[index][val1][val2] != -1) return ghtiya[index][val1][val2];
            for (int i = val1; i <= nums[index]; i++) {
                for (int j = val2; j >= 0; j--) {
                    if (i + j == nums[index]) {
                        result += solve(index + 1, i, j);
                        result = result % MOD;
                    }
                }
            }
            return ghtiya[index][val1][val2] = result;
        };

        return solve(0, 0, nums[0]);

    }
};
