#include <vector>
#include <algorithm>

using namespace std;

class Solution {
private:
    static const int MOD = 1'000'000'007;
    vector<vector<vector<int>>> dpset;

    int fp(const vector<int>& nums, int index, int a, int b) 
    {
        if (index >= nums.size()) 
        {
            return 1;
        }

        if (dpset[index][a][b] != -1) 
        {
            return dpset[index][a][b];
        }

        int count = 0;
        for (int i = 0; i <= nums[index]; ++i) 
        {
            int x = i;
            int y = nums[index] - i;
            if (x >= a && y <= b) {
                count = (count + fp(nums, index + 1, x, y)) % MOD;
            }
        }

        dpset[index][a][b] = count;
        return count;
    }

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dpset = vector<vector<vector<int>>>(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return fp(nums, 0, 0, 50);
    }
};
