class Solution {
    long long mod=1e9+7;
    vector<int> v;
    vector<vector<long long>> dp;
    int n;

    long long rec(int ind,int x)//index, value of first array at ind
    {
        long long ans=0;
        int y=v[ind]-x;
        if(x<0||y<0)return 0;
        if(ind==n-1)return 1;

        if(dp[ind][x]!=-1)return dp[ind][x];

        for(int i=x;i<=v[ind+1];i++)
        {
            int j=v[ind+1]-i;
            if(y>=j){ans+=rec(ind+1,i);}
            ans%=mod;

        }
       // cout<<ind<<" "<<x<<" "<<ans<<endl;
        dp[ind][x]=ans;
        return ans;
    }

public:
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        v.clear();
        for(int i=0;i<n;i++)v.push_back(nums[i]);
        dp.clear();
        dp.resize(n+2);
        for(int i=0;i<n+2;i++)dp[i].assign(52,-1);

        long long ans=0;
        for(int i=0;i<=v[0];i++)
        {
            ans+=rec(0,i);
            ans%=mod;
        }
        return ans;


    }
};
