class Solution {
public:
    const int mod=1e9+7;
    int dp[2001][52][52];
    int helper(vector<int> & nums, int ind, int last1,int last2)
    {
        if(ind==nums.size())return 1;
        if(dp[ind][last1][last2]!=-1)return dp[ind][last1][last2]; 
        int ans=0;
        for(int i=0;i<=nums[ind];i++)
        {
            if((i>=last1) && (nums[ind]-i)<=last2)
            {
                ans=(long(ans)%mod+(long)helper(nums,ind+1,i,nums[ind]-i)%mod)%mod;
            }
        }
        return dp[ind][last1][last2]=ans;
    }
    int countOfPairs(vector<int>& nums) 
    {
        memset(dp,-1,sizeof(dp));
        return helper(nums,0,0,51);    
    }
};
