class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        mm = int(1e9+7)
        endWithKCount = list(range(1, 2 + nums[0]))

        for pn, num in zip(nums, nums[1:]):
            # print(endWithKCount)
            endWithKCountNext = []
            for i in range(num + 1):
                pre_i = min(i, pn - num + i)
                if pre_i < 0:
                    endWithKCountNext.append(0)
                    continue
                endWithKCountNext.append(endWithKCount[pre_i] % mm)
            # print(endWithKCount, endWithKCountNext)
            endWithKCount = list(itertools.accumulate(endWithKCountNext))
        return endWithKCount[-1] % mm
