#define ll long long int
class Solution {
public:
    ll mod = 1e9+7;
    long long f(int i, int l, int r, vector<int>&nums, vector<vector<vector<ll>>>&dp){
        if (i>=nums.size()) return 1;
        ll ans = 0;
        if (dp[i][l][r]!=-1) return dp[i][l][r];
        for (int j=l;j<=nums[i];j++){
            for (int k = r;k>=0;k--){
                if (j+k==nums[i]){
                    ans += f(i+1,j,k,nums,dp);
                    ans = ans%mod;
                }
            }

        }
        return dp[i][l][r] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
            vector<vector<vector<ll>>> dp(n, vector<vector<ll>>(51,vector<ll>(51,-1)));
        return f(0,0, nums[0],nums, dp);

    }
};
