#include <vector>
#include <functional>
using namespace std;

#define VL vector<long long>
#define VVL vector<VL>
#define VVVL vector<VVL>

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        const int MOD = 1e9 + 7;

        VVVL memo(n, VVL(51, VL(51, -1)));

        function<long long(int, int, int)> dp = [&](int idx, int a, int b) -> long long {
            if (idx >= n) return 1;
            if (memo[idx][a][b] != -1) return memo[idx][a][b];

            long long res = 0;

            for (int i = a; i <= nums[idx]; i++) {
                for (int j = b; j >= 0; j--) {
                    if (i + j == nums[idx]) {
                        res = (res + dp(idx + 1, i, j)) % MOD;
                    }
                }
            }

            return memo[idx][a][b] = res;
        };

        return dp(0, 0, nums[0]);
    }
};
