class Solution {
public:
    int MOD = 1e9 + 7;
    vector<vector<vector<int>>> dp;

    int solve(vector<int>& nums, int idx, int pr1, int pr2) {
        if (idx >= nums.size()) return 1;

        if (dp[idx][pr1][pr2] != -1) return dp[idx][pr1][pr2];

        int ans = 0;
        for (int i = 0; i <= nums[idx]; i++) {
            int x = i;
            int y = nums[idx] - x;
            if (x >= pr1 && y <= pr2) {
                ans = (ans + solve(nums, idx + 1, x, y)) % MOD;
            }
        }

        return dp[idx][pr1][pr2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp = vector<vector<vector<int>>>(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return solve(nums, 0, 0, 50);
    }
};
