class Solution {
public:
    const int MOD = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        // int memo[2001][51];
        int n = nums.size();
        vector<vector<int>>memo(n, vector<int>(51, -1));
        function<int(int, int)>dfs = [&](int i, int lastVal1) -> int
        {
            if(i == n)return 1;
            if(memo[i][lastVal1] != -1)return memo[i][lastVal1];
            int ans = 0;
            for(int j = lastVal1; j <= nums[i]; j ++)
            {
                int k = nums[i] - j;
                if(i == 0 || k <= nums[i - 1] - lastVal1)
                    ans = (ans + dfs(i + 1, j)) % MOD;
            }
            return memo[i][lastVal1] = ans;
        };
        int a = dfs(0, 0);
        return a;
    }
};
