class Solution {
    private static final int MOD = 1_000_000_007;
    public int countOfPairs(int[] fuck) {
        int kamr = fuck.length;
        long[][][] momo = new long[kamr][51][51];

        for (long[][] matrix : momo) {

            for (long[] upr : matrix) {

                Arrays.fill(upr, -1);  
            }
        }
        return (int) solve(fuck, 0, 0, fuck[0], momo);
    }

    private long solve(int[] fuck, int choti, int lund1, int lund2, long[][][] momo) {
        if (choti>= fuck.length) return 1;  

        if (momo[choti][lund1][lund2] != -1) return momo[choti][lund1][lund2]; 

        long ass = 0;

        for (int i = lund1; i <= fuck[choti]; i++) {

            for (int j = lund2; j >= 0; j--) {


                if (i + j == fuck[choti]) {

                    ass = (ass + solve(fuck, choti + 1, i, j, momo)) % MOD;
                }
            }
        }
        momo[choti][lund1][lund2] = ass;
        return ass;
    }
}
