class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        m = int(1e9+7)
        dp = [[[-1 for _ in range(52)] for _ in range(52)] for _ in range(n)]
        def solve(idx,r1,r2):
            if idx==n:
                return 1
            if dp[idx][r1][r2]!=-1:
                return dp[idx][r1][r2]
            l = 0
            for i in range(nums[idx]+1):
                if r1<=i and r2>=nums[idx]-i:
                    l +=solve(idx+1,i,nums[idx]-i)
            dp[idx][r1][r2] = l%m
            return dp[idx][r1][r2]
        return solve(0,0,51)%m
