#define ll long long
class Solution {
public:
    ll dp[2001][51][51];
    ll mod = 1000000007;
    ll f(int i , int preva , int prevb , vector<int>&nums ){
        if(i == nums.size()) return 1;

        if(dp[i][preva][prevb] != -1 ) return dp[i][preva][prevb];
        ll ans = 0;

        for(int j = 0 ; j <= nums[i] ; j++ ){
            if((preva == 0 && prevb == 0) || (j >= preva && nums[i]-j <= prevb) )ans=(ans+f(i+1,j,nums[i]-j,nums))%mod;
        }

        return dp[i][preva][prevb]= ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return f(0,0,0,nums);
    }
};
