#define ll long long
class Solution {
public:
    int maxElement;
    int mod = 1e9 + 7;

    int solve(int ind, int prev1, int prev2, int n, vector<vector<vector<ll>>> &dp, vector<int> &nums) {
        if (ind >= n) return 1;

        if (dp[ind][prev1 + 1][prev2 + 1] != -1) return dp[ind][prev1 + 1][prev2 + 1];

        ll ans = 0;

        if (prev1 == -1 && prev2 == -1) {
            for (int i = 0; i <= maxElement; i++) {
                if (i > nums[ind]) break;
                int val1 = i, val2 = nums[ind] - val1;
                ans = (ans + solve(ind + 1, val1, val2, n, dp, nums)) % mod;

            }
        } 
        else {
            for (int i = 0; i <= maxElement; i++) {
                if (i > nums[ind]) break;
                int val1 = i, val2 = nums[ind] - val1;
                if (val1 < prev1 || val2 > prev2) continue;
                ans = (ans + solve(ind + 1, val1, val2, n, dp, nums)) % mod;
            }
        }

        return dp[ind][prev1 + 1][prev2 + 1] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        maxElement = *max_element(nums.begin(), nums.end());

        vector<vector<vector<ll>>> dp(n + 1, vector<vector<ll>>(maxElement + 2, vector<ll>(maxElement + 2, -1)));

        return solve(0, -1, -1, n, dp, nums);
    }
};
