class Solution {
public:
    const int MOD = 1e9 + 7;

vector<vector<vector<int>>> dp;
vector<int> nums;

    int countPairs(int i, int last1, int last2) {
    if (i == nums.size()) {
        return 1; 
    }
    if (dp[i][last1][last2] != -1) {
        return dp[i][last1][last2]; 
    }

    int result = 0;
    for (int arr1_i = last1; arr1_i <= nums[i]; ++arr1_i) {
        int arr2_i = nums[i] - arr1_i;
        if (arr2_i >= 0 && arr2_i <= last2) {
            result = (result + countPairs(i + 1, arr1_i, arr2_i)) % MOD;
        }
    }

    return dp[i][last1][last2] = result;
}

