class Solution {
    public int mod = (int)(1e9+7);
    public int solve(int idx, int nums[], int prev1, int prev2, HashMap<String, Integer> hm){
        if(idx == nums.length) return 1;
        String key = idx+","+prev1+","+prev2;
        if(hm.containsKey(key)) return hm.get(key); 
        int ans = 0;
        for(int i = 0; i <= nums[idx]; i++){
            if(i < prev1) continue;
            if(nums[idx]-i > prev2) continue;
            ans = (ans + solve(idx+1, nums, i, nums[idx]-i, hm))%mod;
        }
        hm.put(key, ans);
        return ans;
    }
    public int countOfPairs(int[] nums) {
        HashMap<String, Integer> hm = new HashMap<>();
        return solve(0, nums, 0, 2000, hm);
    }
}
