class Solution(object):
    MOD = 1000000007

    def __init__(self):
        self.dp = None

    def find_pairs(self, nums, idx, prev1, prev2):
        if idx == len(nums):
            return 1
        if self.dp[idx][prev1][prev2] != -1:
            return self.dp[idx][prev1][prev2]
        count = 0
        for i in range(nums[idx] + 1):
            arr1_val = i
            arr2_val = nums[idx] - i
            if arr1_val >= prev1 and arr2_val <= prev2:
                count = (count + self.find_pairs(nums, idx + 1, arr1_val, arr2_val)) % Solution.MOD
        self.dp[idx][prev1][prev2] = count
        return count

    def countOfPairs(self, nums):
        n = len(nums)
        self.dp = [[[-1] * 51 for _ in range(51)] for _ in range(n)]
        return self.find_pairs(nums, 0, 0, 50)
