class Solution {
public:
    long long mod = 1e9 + 7;
    long long solve(vector<int>& nums, int idx, int prev1, int prev2, vector<vector<vector<int>>>& dp) {
    if (idx == nums.size()) {
        return 1;
    }
        if(dp[idx][prev1][prev2] != -1){
            return dp[idx][prev1][prev2];
        }

    long long count = 0;
    for (int i = prev1; i <= nums[idx]; i++) {
        int arr2_val = nums[idx] - i;
        if (arr2_val <= prev2) {
            count = (count +  solve(nums, idx + 1, i, arr2_val, dp) )% mod;
        }
    }

    return dp[idx][prev1][prev2] = count;
}
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return solve(nums, 0, 0, nums[0], dp);

    }
};
