class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        output = [0]
        @cache
        def f(x,y,node):
            # print(x,y,node)
            if node == len(nums):
                return 1
            else:
                out = 0
                for i in range(x,nums[node]+1):
                    if i>=x and nums[node]-i<=y:
                        out += f(i,nums[node]-i,node+1)
                        out %= 1000000007 
                return out
        return f(0,52,0)
