#define ll long long
class Solution {
public:
    ll mod = 1e9+7;
    int solve(vector<int>& nums, int ind, int a, int b, vector<vector<ll>>& dp){
        if(ind>=nums.size()) return 1;
        if(dp[ind][a]!=-1) return dp[ind][a]%mod;
        ll ans = 0;
        for(int i=a; i<=nums[ind]; i++){
            int j = nums[ind]-i;
            if(j<=b){
                ans = (ans + solve(nums, ind+1, i, j, dp))%mod;
            }
        }
        return dp[ind][a] = ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<ll>> dp(nums.size(), vector<ll>(60, -1));
        return solve(nums, 0, 0, nums[0], dp);
    }
};
