MOD = int(1e9 + 7)

class Solution:
    def __init__(self):
        self.dp = []
        self.nums = []

    def compute(self, idx, start, end):
        if idx >= len(self.nums):
            return 1
        if self.dp[idx][start][end] != -1:
            return self.dp[idx][start][end]

        result = 0
        target = self.nums[idx]

        for i in range(start, target + 1):
            j = target - i
            if 0 <= j <= end:
                result = (result + self.compute(idx + 1, i, j)) % MOD

        self.dp[idx][start][end] = result
        return result

    def countOfPairs(self, input):
        self.nums = input
        size = len(self.nums)
        self.dp = [[[-1] * 51 for _ in range(51)] for _ in range(size)]
        return self.compute(0, 0, self.nums[0])
