import collections

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        P = 10 ** 9 + 7
        old = collections.defaultdict(int)
        for a in range(nums[-1] + 1):
            b = nums[-1] - a
            if b >= 0:
                old[(a,b)] = 1
        for i in range(len(nums)-2, -1, -1):
            new = collections.defaultdict(int)
            for a in range(nums[i] + 1):
                b = nums[i] - a
                for x in range(a, nums[i+1] + 1):
                    y = nums[i+1] - x
                    if y >= 0 and y <= b:
                        new[(a,b)] += old[(x,y)]
                new[(a,b)] %= P
            old = new
        ans = 0
        for v in old.values():
            ans += v
        return ans % P
