class Solution {
public:
    const int mod = 1e9 + 7;
    int solve(int in, int v1, int v2,vector<int>& arr,vector<vector<vector<int>>>& mem) {
        int n=arr.size(),an=0;
        if (n<=in) return 1;
        if (mem[in][v1][v2] != -1) return mem[in][v1][v2];
        for (int i = v1; i <= arr[in]; i++) {
            for (int j = v2; j >= 0; j--) {
                if (i + j != arr[in]) continue;
                int x = solve(in + 1, i, j, arr, mem);
                an = (an + x) % mod;
            }
        }
        return mem[in][v1][v2] = an;
    }

    int countOfPairs(vector<int>& val) {
        int n = val.size();
        vector<vector<vector<int>>>mem(n, vector<vector<int>>(100, vector<int>(100, -1)));
        return solve(0, 0, val[0], val, mem);
    }
};
